import { Component } from '@angular/core';
import { Todo } from 'src/model/todo.model';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  public todos: Todo [] = [];
  public title: String= "Minhas tarefas";

  constructor() {
    this.todos.push(new Todo(1, 'task 1', false));
    this.todos.push(new Todo(2, 'task 2', false));
    this.todos.push(new Todo(3, 'task 3', true));
  }

  remove(todo: Todo){
    const index = this.todos.indexOf(todo);
    if(index !== -1){
      this.todos.splice(index, 1);
    }
  }

  markAsDone(todo: Todo){
    todo.done = true;
  }

  markAsUndone(todo: Todo){
    todo.done = false
  }
}
<h1>{{ title }}</h1>
<ul>
  <li *ngFor="let todo of todos">{{ todo.id }}. {{todo.task}} 
     <button *ngIf="!todo.done" (click)="markAsDone(todo)">concluir</button> 
     <button *ngIf="todo.done" (click)="markAsUndone(todo)">refazer</button> 
     <button (click)="remove(todo)">remover</button></li>
</ul>
<form [FormGroup]="form">
  <fieldset>
    <label>Tarefa</label>
    <input type="text">
    <button>Salvar</button>
  </fieldset>
</form>
